#!/usr/bin/env ruby18 -KU
require ENV["TM_SUPPORT_PATH"] + "/lib/tm/executor" require ENV['TM_SUPPORT_PATH'] + '/lib/ui.rb'
if File.exist?(ENV['TM_PROCESSING'] ? "#{ENV['TM_PROCESSING']}" : "/Applications/Processing.app") processing_java = File.exist?('/usr/local/bin/processing-java') ? "/usr/local/bin/processing-java" : "#{ENV['TM_BUNDLE_SUPPORT']}/bin/processing-java" command = [processing_java, "--sketch=#{File.dirname ENV["TM_FILEPATH"]}", "--output=#{ENV['TMPDIR']}/processing_output", "--force", "--run"] TextMate::Executor.run(command) else TextMate::UI.alert(:critical, "Processing Not Installed", "Please install Processing.app into Applications or set $TM_PROCESSING to it's path.", 'OK') end
